mapper.select:
		

		select * from t_spchu

		WHERE 1=1

		

			 AND spchuDate >= #{sdate,jdbcType=TIMESTAMP} 

		

		

			 AND spchuDate <= #{edate,jdbcType=TIMESTAMP} 

		

		

			

				 AND spchuName LIKE  '%${spchu.spchuName}%' 

			

			

				 AND spchuErweima LIKE  '%${spchu.spchuErweima}%' 

			

			

				 AND spchuId = #{spchu.spchuId} 

			

			

				 AND sptypeId = #{spchu.sptypeId} 

			

			

				 AND spchuType = #{spchu.spchuType} 

			

			

				 AND spchuType1 = #{spchu.spchuType1} 

			

			

				 AND userId = #{spchu.userId} 

			

			

				 AND bumenId = #{spchu.bumenId} 

			

			

				 AND roleId = #{spchu.roleId} 

			

			

				 AND shangpinId = #{spchu.shangpinId} 

			

			

				 AND yhroleId = #{spchu.yhroleId} 

			

			

				 AND yonghuId = #{spchu.yonghuId} 

			

			

				 AND yhbumenId = #{spchu.yhbumenId} 

			
		

		order by spchuId 

		

			limit #{page},#{rows}
