mapper.select:
		

		select * from t_spjin

		WHERE 1=1

		

			 AND spjinDate >= #{sdate,jdbcType=TIMESTAMP} 

		

		

			 AND spjinDate <= #{edate,jdbcType=TIMESTAMP} 

		

		

			

				 AND spjinName LIKE  '%${spjin.spjinName}%' 

			

			

				 AND spjinId = #{spjin.spjinId} 

			

			

				 AND sptypeId = #{spjin.sptypeId} 

			

			

				 AND spjinType = #{spjin.spjinType} 

			

			

				 AND spjinType1 = #{spjin.spjinType1} 

			

			

				 AND userId = #{spjin.userId} 

			

			

				 AND bumenId = #{spjin.bumenId} 

			

			

				 AND roleId = #{spjin.roleId} 

			

			

				 AND shangpinId = #{spjin.shangpinId} 

			

			

				 AND spcangkuId = #{spjin.spcangkuId} 

			

			

				 AND spgysId = #{spjin.spgysId} 

			
		

		order by spjinId 

		

			limit #{page},#{rows}
