mapper.select:
		

		select * from t_sychu

		WHERE 1=1

		

			 AND sychuDate >= #{sdate,jdbcType=TIMESTAMP} 

		

		

			 AND sychuDate <= #{edate,jdbcType=TIMESTAMP} 

		

		

			

				 AND sychuName LIKE  '%${sychu.sychuName}%' 

			

			

				 AND sychuId = #{sychu.sychuId} 

			

			

				 AND sptypeId = #{sychu.sptypeId} 

			

			

				 AND sychuType = #{sychu.sychuType} 

			

			

				 AND sychuType1 = #{sychu.sychuType1} 

			

			

				 AND userId = #{sychu.userId} 

			

			

				 AND bumenId = #{sychu.bumenId} 

			

			

				 AND roleId = #{sychu.roleId} 

			

			

				 AND shangpinId = #{sychu.shangpinId} 

			

			

				 AND yhroleId = #{sychu.yhroleId} 

			

			

				 AND yonghuId = #{sychu.yonghuId} 

			

			

				 AND yhbumenId = #{sychu.yhbumenId} 

			

			

				 AND youkeId = #{sychu.youkeId} 

			
		

		order by sychuId 

		

			limit #{page},#{rows}
