mapper.select:

select yhyhbumenId, yhyhbumenName, yhyhbumenMark, yhyhbumenMark1, yhyhbumenMark2
from t_yhyhbumen
where yhyhbumenId = #{yhyhbumenId,jdbcType=INTEGER}

		

		select * from t_yhbumen

		WHERE 1=1

		

			

				AND yhbumenName LIKE '%${yhbumen.yhbumenName}%'

			

			

				AND yhbumenId = #{yhbumen.yhbumenId}

			

			

				AND yhbumenMark2 = #{yhbumen.yhbumenMark2}

			
		

		order by yhbumenId

		

			limit #{page},#{rows}
