mapper.select:

select * from t_youke
WHERE 1=1

	 AND youkeName = #{youkeName,jdbcType=VARCHAR} 


	 AND youkeDate1 >= #{sdate,jdbcType=TIMESTAMP} 


	 AND youkeDate1 <= #{edate,jdbcType=TIMESTAMP} 


	
		 AND youkeName LIKE  '%${youke.youkeName}%' 
	
	
		 AND youkePassword = #{youke.youkePassword} 
	
	
		 AND youkeId = #{youke.youkeId} 
	
	
		 AND youkeType1 = #{youke.youkeType1} 
	
	
		 AND youkeType2 = #{youke.youkeType2} 
	
	
		 AND yhroleId = #{youke.yhroleId} 
	
	
		 AND youkeSex = #{youke.youkeSex} 
	
	
		 AND yhbumenId = #{youke.yhbumenId} 
	

order by youkeId 

	limit #{page},#{rows}
